{"title":"Franchise Rating Trend (by release date)","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "title": "Franchise Rating Trend (hover shows game)",
  "data": {
    "url": {
      "%context%": true,
      "index": "games",
      "body": {
        "size": 5000,
        "_source": ["name", "aggregated_rating", "release_date", "franchises"]
      }
    },
    "format": {"property": "hits.hits"}
  },
  "transform": [
    {
      "calculate": "toDate(isNumber(datum._source.release_date) ? (datum._source.release_date > 1000000000000 ? datum._source.release_date : datum._source.release_date*1000) : datum._source.release_date)",
      "as": "release_date"
    },
    {"calculate": "datum._source.aggregated_rating", "as": "rating"},
    {"flatten": ["_source.franchises"], "as": ["franchise_id"]},
    {"filter": "isValid(datum.release_date) && isValid(datum.rating) && datum.franchise_id != null"},
    {"joinaggregate": [{"op": "count", "as": "franchise_count"}], "groupby": ["franchise_id"]},
    {"window": [{"op": "dense_rank", "as": "rank"}], "sort": [{"field": "franchise_count", "order": "desc"}]},
    {"filter": "datum.rank <= 5"}
  ],
  "mark": {"type": "line", "point": true},
  "encoding": {
    "x": {
      "field": "release_date",
      "type": "temporal",
      "timeUnit": "year",
      "title": "Release Year"
    },
    "y": {
      "field": "rating",
      "type": "quantitative",
      "title": "Aggregated rating"
    },
    "color": {
      "field": "franchise_id",
      "type": "nominal",
      "title": "Franchise ID"
    },
    "tooltip": [
      {"field": "_source.name", "type": "nominal", "title": "Game"},
      {"field": "franchise_id", "type": "nominal", "title": "Franchise ID"},
      {"field": "rating", "type": "quantitative", "title": "Rating"},
      {"field": "release_date", "type": "temporal", "title": "Release"}
    ]
  }
}
","showWarnings":true,"editorMode":"vega_lite","enableExternalUrls":false}}
